  //     let appleImg = {appleTrue}
  //   } else if (game.apple == false){
  //     let appleImg = {appleFalse}
  //   }
  //   }
  //   console.log(game.name)
  console.log(game.apple)

  return (
    <div className="gamebox">
    <ol className="game">
      <div>
      <h3 className="gametitle"> {game.name} </h3>
      <img className="gameimg" alt="boxart for game" src={game.box_art}/>
      </div>
      <p className="gamerating"> Rating: {game.rating}  </p>
      <p className="gamedescrip"> {game.descrip}  </p>
      <p className="gameget"> Where to get: </p>
      <ul className="storelogos">

      <img className="storeLogo" src={game.macOS ? `${appleTrue}` : `${appleFalse}`} alt="apple logo" />
      <img className="storeLogo" src={game.epic ? `${epicTrue}` : `${appleFalse}`} alt="apple logo" />
      <img className="storeLogo" src={game.macOS ? `${appleTrue}` : `${appleFalse}`} alt="apple logo" />
      <img className="storeLogo" src={game.macOS ? `${appleTrue}` : `${appleFalse}`} alt="apple logo" />
      <img className="storeLogo" src={game.macOS ? `${appleTrue}` : `${appleFalse}`} alt="apple logo" />
      <img className="storeLogo" src={game.macOS ? `${appleTrue}` : `${appleFalse}`} alt="apple logo" />

      <button onClick={()=> toggleGameList()}>Back to Game List</button>


      </ul>
    </ol>
    </div>
  );
}

export default GameCard;
