import { useState } from "react";

function AddGame() {
  const [games, setGames] = useState([]);
  const[display, setDisplay] = UseState(false);
  const [formData, setFormData] = useState({
    name: "",
    box_art: "",
    rating: "",
    genretype: "",
    descrip: "",
    metacritic: "",
    opencritic: "",
    steam: false,
    epic: false,
    macOS: false,
    playstation: false,
    xbox: false,
    nintendo: false,
    genre_id: null,
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const postGame = (game) => {
    fetch("http://localhost:9292/games", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(game),
    })
      .then((res) => res.json())
      .then((newGame) => {
        setGames([newGame, ...games]);
      });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    postGame(formData);
    setFormData({});
    e.reset();
  };

  return (
 <main>
      <h2 className="addGameIntroText"> Add a Game to Our Database</h2>
      {display ?  <h3>Update a Game</h3> 
      :

      <form className="addGameForm" onSubmit={handleSubmit}>
        <h3>Name of Game Here </h3>
        <input
          type="text"
          name="name"
          placeholder="name of game here"
          value={formData.name}
          onChange={handleChange}
        ></input>
        <h3>Boxart Reference Material </h3>
        <input
          type="text"
          name="box_art"
          placeholder="boxart here value="
          value={formData.box_art}
          onChange={handleChange}
        ></input>
        <h3>Rating of Game Here </h3>
        <input
          type="text"
          name="rating"
          placeholder="rating of game of here"
          value={formData.rating}
          onChange={handleChange}
        ></input>
        <h3> Genre Here</h3>
        <input
          type="text"
          name="genretype"
          placeholder="genre here"
          value={formData.genretype}
          onChange={handleChange}
        ></input>
        <h3> Genre ID Here</h3>
        <input
          type="text"
          name="genre_id"
          placeholder="genre id here"
          value={formData.genre_id}
          onChange={handleChange}
        ></input>
        <h3> Description Here</h3>
        <input
          type="text"
          name="descrip"
          placeholder="Description here"
          value={formData.descrip}
          onChange={handleChange}
        ></input>
        <h3> Metacritic Score Here</h3>
        <input
          type="number"
          name="metacritic"
          placeholder="Metacritic score here"
          value={formData.metacritic}
          onChange={handleChange}
        ></input>
        <h3> Opencritic Score Here</h3>
        <input
          type="number"
          name="opencritic"
          placeholder="Opencritic score here"
          value={formData.opencritic}
          onChange={handleChange}
        ></input>
        <h3> What platforms is it available on?</h3>
        <div className="platformBoxes">
          <input
            type="checkbox"
            id="steam"
            name="steam"
            value={formData.steam}
            onChange={handleChange}
          ></input>
          <label for="steam">Steam </label>
          <input
            type="checkbox"
            id="epic"
            name=""
            value={formData.epic}
            onChange={handleChange}
          ></input>
          <labe for="epic">Epic Game Store </labe>
          <input
            type="checkbox"
            id="macOS"
            name=""
            value={formData.macOS}
            onChange={handleChange}
          ></input>
          <label for="macOS">MacOS </label>
          <input
            type="checkbox"
            id="nintendo"
            name=""
            value={formData.nintendo}
            onChange={handleChange}
          ></input>
          <label for="nintendo">Nintendo</label>
          <input
            type="checkbox"
            id="playstation"
            name=""
            value={formData.steam}
            onChange={handleChange}
          ></input>
          <label for="playstation">Playstation </label>
          <input
            type="checkbox"
            id="xbox"
            name=""
            value={formData.xbox}
            onChange={handleChange}
          ></input>
          <label for="xbox">Xbox </label>
          <button onClick={handleSubmit}> Submit Game!</button>
        </div>
      </form> 
      }
    </main>
  );
}

export default AddGame;
