import { useEffect, useState } from "react";
import Games from "./GameCard";
import GameList from "./GameList";

function GameSearch() {
  const [games, setGames] = useState([])
  const [gameView, setGameView] = useState(false)

  useEffect( () => {
    fetch("http://localhost:9292/games")
    .then(r=>r.json())
    .then(fetchedGames => setGames(fetchedGames))
  }, []);

  function toggleGameView(){
    setGameView(!gameView)
  }

  return (
    <>
       <div>
         <h2 className="gamefound"> Games Found: </h2>
        <div className="resultBox"> 
          <ul className="gamesListed"> 
          </ul> 

       {} {games.map( (game) => {
       return <GameList key={game.id} game={game} toggleGameView={toggleGameView} />
        })}
      </div>
      </div>
    </>
  );
}

export default GameSearch;
