// import nintendo_logo from "../images/nintendologosmall.png";
import appleTrue from "../images/apple-true.png"
import appleFalse from "../images/apple-false.png";
import epicTrue from "../images/game_logos/epic-true.png";
import epicFalse from "../images/game_logos/epic-false.png";
import steamTrue from "../images/game_logos/steam-true.png";
import steamFalse from "../images/game_logos/steam-false.png"
import nintendoTrue from "../images/game_logos/nin-true.png"
import nintendoFalse from "../images/game_logos/nin-false.png"
import playstationTrue from "../images/game_logos/ps-true.png";
import playstationFalse from "../images/game_logos/ps-false.png";
import xboxTrue from "../images/game_logos/xbox-true.png";
import xboxFalse from "../images/game_logos/xbox-false.png";



function GameCard( {game, toggleGameList}  ) {

  // let appleImg = false

  // function setLogo(){
  //   if (game.apple == true){
  //     let appleImg = {appleTrue}
  //   } else if (game.apple == false){
  //     let appleImg = {appleFalse}
  //   }
  //   }
  //   console.log(game.name)
  console.log(game.apple)

  return (
    <div className="gamebox">
    <ol className="game">
      <div>
      <h3 className="gametitle"> {game.name} </h3>
      <img className="gameimg" alt="boxart for game" src={game.box_art}/>
      </div>
      <p className="gamerating"> Rating: {game.rating}  </p>
      <div className="gameInfoDiv"> 
      <p className="gamedescrip"> {game.descrip}  </p>
      <div className="gameScores">
      <u><strong>{game.metacritic} </strong> </u>
      <u><strong>{game.opencritic} </strong> </u>
      </div>
      </div>
      <p className="gameget"> Where to get: </p>

      <ul className="storelogos">

      <img className="storeLogo" src={game.steam ? `${steamTrue}` : `${steamFalse}`} alt="steam logo" />
      <img className="storeLogo" src={game.epic ? `${epicTrue}` : `${epicFalse}`} alt="epic logo" />
      <img className="storeLogo" src={game.macOS ? `${appleTrue}` : `${appleFalse}`} alt="apple logo" />
      <img className="storeLogoNintendo" src={game.nintendo ? `${nintendoTrue}` : `${nintendoFalse}`} alt="nintendo logo" />
      <img className="storeLogo" src={game.playstation ? `${playstationTrue}` : `${playstationFalse}`} alt="playstation logo" />

      <img className="storeLogo" src={game.xbox ? `${xboxTrue}` : `${xboxFalse}`} alt="xbox logo" />
      </ul>
      <button onClick={()=> toggleGameList()}>Back to Game List</button>



    </ol>
    </div>
  );
}

export default GameCard;
